import React, { useState } from 'react';
import './App.css';

import { MuiThemeProvider, createMuiTheme, makeStyles } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';

import ButtonBase from '@material-ui/core/ButtonBase';

const useStyles = makeStyles(theme => ({
    AppDiv: {
        position: 'absolute',
        margin: 0,
        width: '100%',
        height: '100%',
        background: 'radial-gradient(ellipse at center, rgba(8, 25, 42, 1) 19%, rgba(1, 4, 6, 1) 100%)',
        zIndex: -3,
    },
    AppStarsDiv: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        overflow: 'hidden',
        zIndex: -2,
    },
    AppStar: {
        zIndex: '-1',
        filter: 'blur(1px)',
        position: 'absolute',
        background: 'radial-gradient(ellipse at center, rgba(177, 198, 219, 1) 2%, rgba(5, 63, 118, 1) 100%)',
        borderRadius: '100%',
        animation: `$shine infinite alternate`,
        zIndex: -1,
    },
    image: {
        position: 'relative',
        left: '50%',
        //top: '50%',
        transform: 'translate(-50%, 50%)',
        width: '20vw !important', // Overrides inline-style
        height: '20vw',
        borderRadius: '50%',
    },
    MoonDiv: {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
    },
    MoonSrc: {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
    },
    "@keyframes shine": {
        "0%": {
            transform: 'scale(1)',
            opacity: '1',
        },
        "20%": {
            transform: 'scale(.9)',
            opacity: '.8',
        },
        "40%": {
            transform: 'scale(1)',
            opacity: '.9',
        },
        "40%": {
            transform: 'scale(.2)',
            opacity: '.2',
        },
        "60%": {
            transform: 'scale(.2)',
            opacity: '.1',
        },
        "80%": {
            transform: 'scale(.5)',
            opacity: '.5',
        },
        "100%": {
            transform: 'scale(.9)',
            opacity: '.9',
        },
    },
}));

const params = {
    amount: 200,
    size: {
        min: 1,
        max: 5,
        giant: 9
    },
    duration: {
        min: 5,
        max: 25,
    }
}

function randomBetween(a, b) {
    return (a + (Math.random() * (b - a)));
}

export default function App() {
    const classes = useStyles();
    const [theme, setTheme] = useState({
        palette: {
            type: "dark",
            primary: { main: "#3f51b5" },
            secondary: { main: "#f50057" },
        }
    });

    const switchMode = () => {
        let newPaletteType = theme.palette.type === "light" ? "dark" : "light";
		let primaryColor = theme.palette.type === "light" ? "#f9a825" : "#3f51b5";
		let secondaryColor = theme.palette.type === "light" ? "#f50057" : "#f50057";
		setTheme({
			palette: {
				type: newPaletteType,
				primary: { main: primaryColor },
				secondary: { main: secondaryColor },
			}
		});
    }

    let arrayStars = [];
    const muiTheme = createMuiTheme(theme);

    for (let i = 0; i < params.amount; i++) {
        let size = Math.round(Math.random() * 10) === 0 ? params.size.giant : randomBetween(params.size.min, params.size.max);
        arrayStars.push(
            <div className={classes.AppStar}
                key={i}
                style={{
                    width: size + "px",
                    height: size + "px",
                    left: randomBetween(0, 100) + "%",
                    top: randomBetween(0, 100) + "%",
                    boxShadow: "0 0 " + size + "px " + size / 2 + "px #043668",
                    animationDuration: randomBetween(params.duration.min, params.duration.max) + "s",
                }
                } 
            />
        );
    }

    return (
        <MuiThemeProvider theme={muiTheme}>
            <CssBaseline />
            {theme.palette.type === "light" ? (
                <div>

                </div>
            ) : (
                <div className={classes.AppDiv}>
                    <div className={classes.AppStarsDiv}>
                        {arrayStars}
                    </div>
                </div>
            )
            }
            <ButtonBase className={classes.image}
                type='button'
                focusRipple
                onClick={ () => { switchMode() } }>
                <div className={classes.MoonDiv}>
                    <img className={classes.MoonSrc}
                        src={'https://i.ya-webdesign.com/images/moon-icon-png-8.png'}
                        alt={'Moon'}>
                    </img>
                </div>
            </ButtonBase>
        </MuiThemeProvider>
    );
}
