import React, { useState } from 'react';
import './App.css';

import { MuiThemeProvider, createMuiTheme, makeStyles } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';

import ButtonBase from '@material-ui/core/ButtonBase';

const useStyles = makeStyles(theme => ({
    AppDiv: {
        position: 'absolute',
        margin: 0,
        width: '100%',
        height: '100%',
        background: 'radial-gradient(ellipse at center, rgba(8, 25, 42, 1) 19%, rgba(1, 4, 6, 1) 100%)',
        zIndex: -3,
    },
    AppStarsDiv: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        overflow: 'hidden',
        zIndex: -2,
    },
    AppStar: {
        zIndex: -1,
    },
    image: {
        position: 'relative',
        //height: 200,
        [theme.breakpoints.down('xs')]: {
            //width: '100% !important', // Overrides inline-style
            //height: 100,
        },
        '&:hover, &$focusVisible': {
            zIndex: 1,
            '& $imageBackdrop': {
                opacity: 0.15,
            },
            '& $imageMarked': {
                opacity: 0,
            },
            '& $imageTitle': {
                border: '4px solid currentColor',
            },
        },
    },
    focusVisible: {},
    MoonDiv: {
        position: 'absolute',
        width: '30vw',
        height: '30vw',
    },
    MoonSrc: {
        position: 'absolute',
        width: '100%',
        height: '100%',
    },
}));

const params = {
    amount: 200,
    size: {
        min: 1,
        max: 5,
        giant: 9
    },
    duration: {
        min: 5,
        max: 25,
    }
}

function randomBetween(a, b) {
    return (a + (Math.random() * (b - a)));
}

export default function App() {
    const classes = useStyles();
    const [theme, setTheme] = useState({
        palette: {
            type: "light",
            primary: { main: "#3f51b5" },
            secondary: { main: "#f50057" },
        }
    });

    let arrayStars = [];
    const muiTheme = createMuiTheme(theme);

    for (let i = 0; i < params.amount; i++) {
        let size = Math.round(Math.random() * 10) === 0 ? params.size.giant : randomBetween(params.size.min, params.size.max);
        arrayStars.push(
            <div className={classes.AppStar}
                key={i}
                style={{
                    width: size + "px",
                    height: size + "px",
                    left: randomBetween(0, 100) + "%",
                    top: randomBetween(0, 100) + "%",
                    boxShadow: "0 0 " + size + "px " + size / 2 + "px #043668",
                    animationDuration: randomBetween(params.duration.min, params.duration.max) + "s",
                }} />
        );
    }

    return (
        <MuiThemeProvider theme={muiTheme}>
            <CssBaseline />
            {/* <div className={classes.AppDiv}>
                <div className={classes.AppStarsDiv}>
                    {arrayStars}
                </div>
            </div> */}
            <ButtonBase className={classes.image}
                focusVisibleClassName={classes.focusVisible}
                focusRipple>
                <div className={classes.MoonDiv}>
                    <img className={classes.MoonSrc}
                        src={'https://i.ya-webdesign.com/images/moon-icon-png-8.png'}
                        alt={'Moon'}>
                    </img>
                </div>
            </ButtonBase>
        </MuiThemeProvider>
    );
}
