import { SET_LANG } from '../actions/Actions'
import RuDict from '../dictionary/ru';
import EnDict from '../dictionary/en';

const initialState = {
  currLang: EnDict,
}

export function currLangReduceer(state = initialState, action) {
  switch (action.type) {
    case SET_LANG:
      return { ...state, currLang: action.currLangState }

    default:
      return state
  }
}
