import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import './App.css';

const useStyles = makeStyles(theme => ({
    AppDiv: {
        position: 'absolute',
        margin: 0,
        width: '100%',
        height: '100%',
        background: 'radial-gradient(ellipse at center, rgba(8, 25, 42, 1) 19%, rgba(1, 4, 6, 1) 100%)',
    },
    AppStarsDiv: {
        position: 'absolute',
        width: '100%',
        height: '100%',
        overflow: 'hidden',
    },
}));

export default function App() {
    const classes = useStyles();

    const params = {
        amount: 200,
        size: {
            min: 1,
            max: 5,
            giant: 9
        },
        duration: {
            min: 5,
            max: 25,
        }
    }

    const randomBetween = (a, b) => {
        return (a + (Math.random() * (b - a)));
    }


    return (
        <div className={classes.AppDiv}>
            <div className={classes.AppStarsDiv}>
                {() => {
                    for (let i = 0; i < params.amount; i++) {
                        let size = Math.round(Math.random() * 10) === 0 ? params.size.giant : randomBetween(params.size.min, params.size.max);
                        <div>
                            Hello
                        </div>
                    }
                }}
            </div>
        </div>
    );
}
